    const clientMessage = new ClientMessage();
    clientMessage.setA(params.getA());
    clientMessage.setB(params.getB());
    clientMessage.setC(params.getC());
    clientMessage.setD(params.getD());
    client.send(
        clientMessage,
        undefined,
        (err: any, response: ServerMessage) => {
            if (err) {
